        msg.payload = JSON.parse(msg.topicTicket)
        const status = { fill: "green", shape: "dot", text: "OK"}
        node.status(status)
        msg[`${node.name}-${node.id}`] = {
            id: node.id,
            name: node.name,
            payload: msg.payload,
            status: status
        }
    } catch (error) {
        const status = { fill: "yellow", shape: "dot", text: "not json data" }
        node.status(status)
        msg[`${node.name}-${node.id}`] = {
            id: node.id,
            name: node.name,
            payload: msg.payload,
            status: status
        }
    }
}

return msg;

