const endpointport = env.get("endpointport")

const topicTicketPath = env.get("topicTicketPath")
const topicTicket = encodeURIComponent(JSON.stringify(RED.util.getPropByPath(msg, topicTicketPath)))

msg.joburl = `http://localhost:${endpointport}/addtopic/${topicTicket}`
