return msg

if (msg.statusCode == "200") {
    if (msg.payload) {
        try {
            msg.payload = JSON.parse(msg.payload)
            const status = { fill: "green", shape: "dot", text: "OK" }
            node.status(status)
            msg[`${node.name}-${node.id}`] = {
                id: node.id,
                name: node.name,
                payload: msg.payload,
