const jobsessionPath = env.get("jobsessionPath") //ex.msg.payload.topic 
return RED.util.setPropByPath(msg, jobsessionPath, jobsession)

const targetKeyPath = env.get("targetKeyPath")
const target = RED.util.getPropByPath(msg, targetKeyPath)

const basePath = env.get("basePath")
const targetpath = path.join(basePath, target)

try {
    if (fs.existsSync(targetpath)) {
        const status = { fill: "green", shape: "dot", text: `exists: ${targetpath}` }
        node.status(status)
        msg[`${node.name}-${node.id}`] = {
            id: node.id,
            name: node.name,
            payload: `${targetpath}が見つかった`,
            status: status
        } 
        node.log(`${targetpath}が見つかった`)
        return [msg, null, null]
    } else {
        const status = { fill: "yellow", shape: "dot", text: `not exists: ${targetpath}` }
        node.status(status)
        msg[`${node.name}-${node.id}`] = {
            id: node.id,
            name: node.name,
            payload: `${targetpath}が見つからない`,
            status: status
        }
        node.log(`${targetpath}が見つからない`)
        return [null, msg, null]
    }
} catch (error) {
    const status = { fill: "red", shape: "dot", text: `error: ${targetpath}` }
    node.status(status)
    msg[`${node.name}-${node.id}`] = {
        id: node.id,
        name: node.name,
        payload: `error ${targetpath}`,
        error: error,
        status: status
    }
    node.error(`${targetpath} error: ${error}`, error)
    return [null, null, msg]
};

