msg.exec = `df ${targetpath}`
msg._payload = msg.payload

const status = { fill: "green", shape: "dot", text: `df: ${targetpath}` }
node.status(status)
msg[`${node.name}-${node.id}`] = {
    id: node.id,
    name: node.name,
    exec: msg.exec,
    status: status
}

return msg;

const regex = /.*\s+([0-9]+)\s+([0-9]+)\s+([0-9]+)\s+([0-9]+)%.*/
const match = msg.payload.match(regex)
if (match) {
    msg.df = {
        match: match,
        size: Number(match[1]),
        used: Number(match[2]),
        available: Number(match[3]),
        usePer: Number(match[4])
    }
}

msg.payload = msg._payload
msg._payload = null
return msg;

const targetKeyPath = env.get("targetKeyPath")
const target = RED.util.getPropByPath(msg, targetKeyPath)

const basePath = env.get("basePath")
const targetpath = path.join(basePath, target)
msg.targetpath = targetpath
msg.exec = `rm -R ${targetpath }`
msg._payload = msg.payload

const status = { fill: "green", shape: "dot", text: `rm: ${targetpath}` }
node.status(status)
msg[`${node.name}-${node.id}`] = {
    id: node.id,
    name: node.name,
    exec: msg.exec,
    status: status
}

return msg;

if (msg.payload){
    msg.payload = msg._payload
    msg._payload = null
    const status = { fill: "green", shape: "dot", text: `deleted: ${msg.targetpath}` }
    node.status(status)
    return [msg,null]
} else {
    msg.payload = msg._payload
    msg._payload = null
    const status = { fill: "red", shape: "dot", text: `error: ${msg.targetpath}` }
    node.status(status)
